pragma solidity ^0.8.20;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {Pausable} from "@openzeppelin/contracts/utils/Pausable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import "./interfaces/IPaythenaCore.sol";

contract PaythenaAutomation is ReentrancyGuard, AccessControl, Pausable {
    using Address for address;

    bytes32 public constant AUTOMATION_ROLE = keccak256("AUTOMATION_ROLE");

    address public immutable core;
    uint256 public lastProcessedTime;
    uint256 public constant PROCESSING_INTERVAL = 1 days;
    uint256 public constant MAX_BATCH_SIZE = 50;

    mapping(address => bool) public automatedCompanies;

    event PaymentsProcessed(
        uint256 processedCount,
        uint256 timestamp
    );

    event AutomationConfigured(
        address indexed core,
        uint256 timestamp
    );

    event AutomationEnabled(address indexed company);
    event AutomationDisabled(address indexed company);

    constructor(address _core) {
        require(_core != address(0), "Invalid core address");
        require(_core.code.length > 0, "Core must be contract");

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(AUTOMATION_ROLE, msg.sender);

        core = _core;
        lastProcessedTime = block.timestamp;

        emit AutomationConfigured(_core, block.timestamp);
    }

    function processPaymentBatch() 
        external 
        nonReentrant 
        whenNotPaused 
        onlyRole(AUTOMATION_ROLE) 
        returns (uint256)
    {
        require(
            block.timestamp >= lastProcessedTime + PROCESSING_INTERVAL,
            "Too early"
        );

        IPaythenaCore coreContract = IPaythenaCore(core);
        uint256 processedCount = 0;

        // Process payments for each company
        address[] memory companies = coreContract.getActiveCompanies();
        for (uint256 i = 0; i < companies.length && processedCount < MAX_BATCH_SIZE; i++) {
            address company = companies[i];
            if (!coreContract.canProcessPayment(company)) continue;

            address[] memory contributors = coreContract.getActiveContributors(company);
            for (uint256 j = 0; j < contributors.length && processedCount < MAX_BATCH_SIZE; j++) {
                address contributor = contributors[j];
                if (coreContract.isPaymentDue(company, contributor)) {
                    try coreContract.processSalary(contributor) {
                        processedCount++;
                    } catch {
                        continue;
                    }
                }
            }
        }

        lastProcessedTime = block.timestamp;
        emit PaymentsProcessed(processedCount, block.timestamp);
        return processedCount;
    }

    function pause() 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _pause();
    }

    function unpause() 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _unpause();
    }

    function enableAutomation() external {
        require(!automatedCompanies[msg.sender], "Already automated");
        automatedCompanies[msg.sender] = true;
        emit AutomationEnabled(msg.sender);
    }

    function disableAutomation() external {
        require(automatedCompanies[msg.sender], "Not automated");
        automatedCompanies[msg.sender] = false;
        emit AutomationDisabled(msg.sender);
    }
}","keccak256":"0xcc17c25be05cd4a60c47ab7d14d0e103e758bdd239bc8ed11e5a1de165fe5721","license":"MIT"},"contracts/interfaces/IPaythenaCore.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IPaythenaCore {
    // Structs
    struct Contributor {
        string name;
        uint256 salary;
        uint256 lastPayment;
        uint256 nextPayment;
        uint256 paymentFrequency;
        uint256 lastProcessedTime;
        bool isActive;
    }

    struct Company {
        string name;
        uint256 balance;
        uint256 contributorCount;
        bool isActive;
        address admin;
        mapping(address => Contributor) contributors;
        address[] contributorAddresses;
    }

    // Role functions
    function hasRole(bytes32 role, address account) external view returns (bool);
    function getRoleAdmin(bytes32 role) external view returns (bytes32);
    function grantRole(bytes32 role, address account) external;
    function revokeRole(bytes32 role, address account) external;
    function renounceRole(bytes32 role, address account) external;

    // Role constants
    function COMPANY_ROLE() external view returns (bytes32);
    function CONTRIBUTOR_ROLE() external view returns (bytes32);
    function AUTOMATION_ROLE() external view returns (bytes32);
    function INTEGRATION_ROLE() external view returns (bytes32);

    // Integration functions
    function setAutomationContract(address _automationContract) external;
    function getStakedAmount(address account) external view returns (uint256);
    function getLoanBalance(address account) external view returns (uint256);
    function processPaymentBatch(address[] calldata contributors) external returns (uint256);

    // Events
    event CompanyRegistered(address indexed company, string name, uint256 timestamp);
    event ContributorAdded(address indexed company, address indexed contributor, string name, uint256 salary, uint256 timestamp);
    event ContributorRemoved(address indexed company, address indexed contributor, uint256 timestamp);
    event SalaryProcessed(address indexed company, address indexed contributor, uint256 amount, uint256 timestamp);
    event FundsDeposited(address indexed company, uint256 amount, uint256 timestamp);
    event FundsWithdrawn(address indexed company, uint256 amount, uint256 timestamp);
    event PaymentProcessed(uint256 indexed paymentId, address indexed company, address indexed contributor, uint256 amount, bytes32 txHash, uint256 timestamp);
    event AutomationContractUpdated(address indexed oldContract, address indexed newContract);
    event IntegrationError(string reason, bytes data);

    // Core functions
    function registerCompany(string calldata name) external;
    function addContributor(address contributor, string calldata name, uint256 salary, uint256 paymentFrequency) external;
    function removeContributor(address contributor) external;
    function processSalary(address contributor) external;
    function deposit(uint256 amount) external;
    function withdraw(uint256 amount) external;

    // View functions
    function getCompanyDetails(address company) external view returns (
        string memory name,
        uint256 balance,
        uint256 contributorCount,
        bool isActive,
        address admin
    );

    function getContributorDetails(address company, address contributor) external view returns (
        string memory name,
        uint256 salary,
        uint256 nextPayment,
        uint256 paymentFrequency,
        bool isActive,
        uint256 lastProcessedTime
    );

    function isPaymentDue(address company, address contributor) external view returns (bool);
    function canProcessPayment(address company) external view returns (bool);
    function getActiveCompanies() external view returns (address[] memory);
    function getActiveContributors(address company) external view returns (address[] memory);
    function getTotalPayroll(address company) external view returns (uint256);
}","keccak256":"0xaad3a12adbe6cf187db0db95057eff083e2265f8e0714d9686a374ef767e3e63","license":"MIT"}},"version":1}
